import fs from "fs";
import path from "path";
import satori from "satori";
import { html as __html } from "satori-html";
import sharp from "sharp";
import { fileURLToPath } from "url";

import type { UserProfileProps } from "../types/interfaces";

const __dirname = path.dirname(fileURLToPath(import.meta.url));
const robotoArrayBuffer = new Uint8Array(
  fs.readFileSync(path.join(__dirname, "Roboto-Regular.ttf"))
).buffer;

export default async function main(user: UserProfileProps): Promise<Buffer> {
  const markup = __html`
    <main
      class="flex"
      style=" margin: 0; padding: 0; display: flex;"
    >
    <div class="wrapper" style="background-image: linear-gradient(0deg, purple 0%, blue 25%, green 100%); display:flex; width: 596px;  justify-content:center; margin: 0; padding:1rem;">
      <div
        class="card"
        style=" margin: 0; padding: 2rem;  display: flex; border-radius: 0.5rem; gap: 2rem; flex:1;  background-color: rgba(255, 255, 255, 0.5); align-items:center; justify-content: center; flex-direction:column;"
      >
        <div
          class="card__user"
          style=" margin: 0; padding: 0; text-align: center; display:flex; flex-direction: column; align-items: center; gap: 1rem; justify-content: center;"
        >
          <img
            class="card__user__avatar"
            src="${user.avatar}"
            width="192"
            height="192"
            style=" margin: 0; padding: 0; max-width: 100%; border-radius: 50%; border: 0.25rem solid rgba(0, 0, 0, 0.7); object-fit: cover;"
          />
          <div
            class="card__user__name"
            style=" margin: 0; padding: 0; text-transform: uppercase; letter-spacing: 0.5rem; font-weight: 300; font-size: 1.5rem; display:flex; flex-direction: column;"
          >
          ${user.name}
          </div>
        </div>
        <div
          class="card__info"
          style=" margin: 0; padding: 1.5rem;  color: white; border-radius: 0.5rem; font-weight: 300; letter-spacing: 0.25rem; font-size: 1.25rem; display:flex; flex-direction:column; background-color: rgba(0, 0, 0, 0.7)"
        >
          <div
            class="card__info__row"
            style="margin: 0; padding: 0; display: flex; align-items: center; gap: 0.5rem; "
          >
          <img src="data:image/png;base64," width="32" height="32" style="filter: invert(1);"/> <p>${user.characters} characters</p>
          </div>
          <div
            class="card__info__row"
            style=" margin: 0; padding: 0; display: flex; align-items: center; gap: 0.5rem; "
          >
          <img src="data:image/png;base64," width="32" height="32" style="filter: invert(1);"/> <p>${user.posts} posts</p>
          </div>
          <div
            class="card__info__row"
            style=" margin: 0; padding: 0; display: flex; align-items: center; gap: 0.5rem;"
          >
          <img src="data:image/png;base64," width="32" height="32" style="filter: invert(1);"/> <p>${user.letters} letters</p>
          </div>
          <div
            class="card__info__row"
            style="margin: 0; padding: 0; display: flex; align-items: center; gap: 0.5rem;"
          >
          <img src="data:image/png;base64," width="32" height="32" style="filter: invert(1);"/> <p>${user.likes} likes</p>
          </div>
        </div>

      </div>
</div>
    </main>
  `;
  const svg = await satori(markup, {
    fonts: [
      {
        data: robotoArrayBuffer,
        name: "Roboto",
        style: "normal",
        weight: 400,
      },
    ],
    width: 596,
  });
  const svgBuffer = Buffer.from(svg);
  const pngBuffer = await sharp(svgBuffer).png().toBuffer();
  return pngBuffer;
}
